  cormat[i, 2] <- cor(x[, target], x$S100comp, method = "spearman")
  cormat[i, 3] <- cor(x[, target], x$CD45comp, method = "spearman")
  cormat[i, 4] <- cor(x[, target], x$CD68comp, method = "spearman")
}


