           legend.labs = c(paste0("Low (n = ", table(dat$group)['Low'], ")"),
                           paste0("High (n = ", table(dat$group)['High'], ")"))) + 
  xlab("Time (days)") + ylab("Overall Survival") -> p

p$plot +
  geom_label(label = expression("logrank "*italic(P)*" < 0.0001"), 
             x = 250, y = 0.1, label.size = NA)  
  

